body {
    color: #FFFFFF;
    background-color: #4F87A0;
    font-family: sans;
    font-size: medium;
}
a {
    color: #000000;
    background-color: #FFFFFF;
    text-decoration: none;
    font-weight: bold;
}
p {
    padding: 0.75em;
    margin: 0;
}
a:hover {
    text-decoration: underline;
}
#header {
    border: thick solid #000000;
    background-color: #FFFFFF;
    color: #000000;
    margin-bottom: 0.5em;
    text-align: center;
}
#header img {
    margin-left: auto;
    margin-right: auto;
}
#top {
    margin: 1em;
    text-align: center;
}
#days {
    display: inline;
    border: thick solid #000000;
    background-color: #FFFFFF;
    color: #000000;
    margin: 0.125em;
    padding: 0.3em;
    margin-bottom: 0.5em;
    text-align: center;
}
#nav {
    display: inline;
    border: thick solid #000000;
    background-color: #FFFFFF;
    color: #000000;
    margin: 0.125em;
    padding: 0.3em;
    margin-bottom: 0.5em;
    text-align: center;
    font-weight: bold;
}
#content {
    border: thick solid #000000;
    background-color: #FFFFFF;
    color: #000000;
    overflow: auto;
    padding: 0.375em;
    zoom: 1;
}
#content p {
    padding: 0.375em;
}
#content img {
    margin: 0.375em;
}
#calendar {
    border: 0;
}
#content img {
    border: thick solid black;
    margin: 1em;
}
div.left {
    float: left;
}
div.right {
    float: right;
}
div.side {
    width: 50%;
    text-align: center;
}
